  const endPoint = 3; //질문의 갯수
  const navigate = useNavigate();

  const [e,setE] = useState(0);
  const [i,setI] = useState(0);
  const [n,setN] = useState(0);
  const [s,setS] = useState(0);
  const [f,setF] = useState(0);
  const [t,setT] = useState(0);
  const [j,setJ] = useState(0);
  const [p,setP] = useState(0);


  const Button = ({answer , score}) => {
    //버튼을 클릭했을때 실행되는 함수

    const goNext = () => {
      console.log({answer});
      console.log(idx)
      if(endPoint !== idx + 1){
        setIdx(idx+1);
      }
      else{ //결과 페이지 이동
        navigate("/result");
      }
  }

    const sumScore = () => {
      console.log({score});
      console.log(s);
      switch(score){
        case 'E':
          setE(e+1);
          break;
        case 'I':
          setI(i+1);
          break;
        case 'N':
          setN(n+1);
          break;
        case 'S':
          setS(s+1);
          break;
        case 'T':
          setT(t+1);
          break;
        case 'F':
          setF(f+1);
          break;
        case 'J':
          setJ(j+1);
          break;
        case 'P':
          setP(p+1);
          break;   
      }
    }

    return(
      <>
        <button onClick={()=>{
          goNext()
          sumScore()
          }
        }>
        {answer}</button>
      </>
    );
  }

  return( 
      <div>
          <p>{Data[idx].q}</p>
          <Button answer={Data[idx].a[0].answer} score={Data[idx].a[0].type} />
          <Button answer={Data[idx].a[1].answer} score={Data[idx].a[1].type} />
          {/*<button onClick={() => goNext(score = Data[idx].a[0].type)}>
